            array('id' => '511','chapter_id' => '4','arabic_name' => 'وَلَيْسَتِ ٱلتَّوْبَةُ لِلَّذِينَ يَعْمَلُونَ ٱلسَّيِّـَٔاتِ حَتَّىٰٓ إِذَا حَضَرَ أَحَدَهُمُ ٱلْمَوْتُ قَالَ إِنِّى تُبْتُ ٱلْـَٔـٰنَ وَلَا ٱلَّذِينَ يَمُوتُونَ وَهُمْ كُفَّارٌ ۚ أُو۟لَـٰٓئِكَ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا','verse_number' => '18','verse_key' => '4:18','hizb_number' => '8','rub_el_hizb_number' => '32','ruku_number' => '64','manzil_number' => '1','sajdah_number' => '0','page_number' => '80','juz_number' => '4','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '512','chapter_id' => '4','arabic_name' => 'يَـٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا يَحِلُّ لَكُمْ أَن تَرِثُوا۟ ٱلنِّسَآءَ كَرْهًا ۖ وَلَا تَعْضُلُوهُنَّ لِتَذْهَبُوا۟ بِبَعْضِ مَآ ءَاتَيْتُمُوهُنَّ إِلَّآ أَن يَأْتِينَ بِفَـٰحِشَةٍ مُّبَيِّنَةٍ ۚ وَعَاشِرُوهُنَّ بِٱلْمَعْرُوفِ ۚ فَإِن كَرِهْتُمُوهُنَّ فَعَسَىٰٓ أَن تَكْرَهُوا۟ شَيْـًٔا وَيَجْعَلَ ٱللَّهُ فِيهِ خَيْرًا كَثِيرًا','verse_number' => '19','verse_key' => '4:19','hizb_number' => '8','rub_el_hizb_number' => '32','ruku_number' => '64','manzil_number' => '1','sajdah_number' => '0','page_number' => '80','juz_number' => '4','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '513','chapter_id' => '4','arabic_name' => 'وَإِنْ أَرَدتُّمُ ٱسْتِبْدَالَ زَوْجٍ مَّكَانَ زَوْجٍ وَءَاتَيْتُمْ إِحْدَىٰهُنَّ قِنطَارًا فَلَا تَأْخُذُوا۟ مِنْهُ شَيْـًٔا ۚ أَتَأْخُذُونَهُۥ بُهْتَـٰنًا وَإِثْمًا مُّبِينًا','verse_number' => '20','verse_key' => '4:20','hizb_number' => '8','rub_el_hizb_number' => '32','ruku_number' => '64','manzil_number' => '1','sajdah_number' => '0','page_number' => '81','juz_number' => '4','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '514','chapter_id' => '4','arabic_name' => 'وَكَيْفَ تَأْخُذُونَهُۥ وَقَدْ أَفْضَىٰ بَعْضُكُمْ إِلَىٰ بَعْضٍ وَأَخَذْنَ مِنكُم مِّيثَـٰقًا غَلِيظًا','verse_number' => '21','verse_key' => '4:21','hizb_number' => '8','rub_el_hizb_number' => '32','ruku_number' => '64','manzil_number' => '1','sajdah_number' => '0','page_number' => '81','juz_number' => '4','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '515','chapter_id' => '4','arabic_name' => 'وَلَا تَنكِحُوا۟ مَا نَكَحَ ءَابَآؤُكُم مِّنَ ٱلنِّسَآءِ إِلَّا مَا قَدْ سَلَفَ ۚ إِنَّهُۥ كَانَ فَـٰحِشَةً وَمَقْتًا وَسَآءَ سَبِيلًا','verse_number' => '22','verse_key' => '4:22','hizb_number' => '8','rub_el_hizb_number' => '32','ruku_number' => '64','manzil_number' => '1','sajdah_number' => '0','page_number' => '81','juz_number' => '4','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '516','chapter_id' => '4','arabic_name' => 'حُرِّمَتْ عَلَيْكُمْ أُمَّهَـٰتُكُمْ وَبَنَاتُكُمْ وَأَخَوَٰتُكُمْ وَعَمَّـٰتُكُمْ وَخَـٰلَـٰتُكُمْ وَبَنَاتُ ٱلْأَخِ وَبَنَاتُ ٱلْأُخْتِ وَأُمَّهَـٰتُكُمُ ٱلَّـٰتِىٓ أَرْضَعْنَكُمْ وَأَخَوَٰتُكُم مِّنَ ٱلرَّضَـٰعَةِ وَأُمَّهَـٰتُ نِسَآئِكُمْ وَرَبَـٰٓئِبُكُمُ ٱلَّـٰتِى فِى حُجُورِكُم مِّن نِّسَآئِكُمُ ٱلَّـٰتِى دَخَلْتُم بِهِنَّ فَإِن لَّمْ تَكُونُوا۟ دَخَلْتُم بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْ وَحَلَـٰٓئِلُ أَبْنَآئِكُمُ ٱلَّذِينَ مِنْ أَصْلَـٰبِكُمْ وَأَن تَجْمَعُوا۟ بَيْنَ ٱلْأُخْتَيْنِ إِلَّا مَا قَدْ سَلَفَ ۗ إِنَّ ٱللَّهَ كَانَ غَفُورًا رَّحِيمًا','verse_number' => '23','verse_key' => '4:23','hizb_number' => '8','rub_el_hizb_number' => '32','ruku_number' => '65','manzil_number' => '1','sajdah_number' => '0','page_number' => '81','juz_number' => '4','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '517','chapter_id' => '4','arabic_name' => '۞ وَٱلْمُحْصَنَـٰتُ مِنَ ٱلنِّسَآءِ إِلَّا مَا مَلَكَتْ أَيْمَـٰنُكُمْ ۖ كِتَـٰبَ ٱللَّهِ عَلَيْكُمْ ۚ وَأُحِلَّ لَكُم مَّا وَرَآءَ ذَٰلِكُمْ أَن تَبْتَغُوا۟ بِأَمْوَٰلِكُم مُّحْصِنِينَ غَيْرَ مُسَـٰفِحِينَ ۚ فَمَا ٱسْتَمْتَعْتُم بِهِۦ مِنْهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِيضَةً ۚ وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا تَرَٰضَيْتُم بِهِۦ مِنۢ بَعْدِ ٱلْفَرِيضَةِ ۚ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمًا','verse_number' => '24','verse_key' => '4:24','hizb_number' => '9','rub_el_hizb_number' => '33','ruku_number' => '65','manzil_number' => '1','sajdah_number' => '0','page_number' => '82','juz_number' => '5','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
            array('id' => '518','chapter_id' => '4','arabic_name' => 'وَمَن لَّمْ يَسْتَطِعْ مِنكُمْ طَوْلًا أَن يَنكِحَ ٱلْمُحْصَنَـٰتِ ٱلْمُؤْمِنَـٰتِ فَمِن مَّا مَلَكَتْ أَيْمَـٰنُكُم مِّن فَتَيَـٰتِكُمُ ٱلْمُؤْمِنَـٰتِ ۚ وَٱللَّهُ أَعْلَمُ بِإِيمَـٰنِكُم ۚ بَعْضُكُم مِّنۢ بَعْضٍ ۚ فَٱنكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّ وَءَاتُوهُنَّ أُجُورَهُنَّ بِٱلْمَعْرُوفِ مُحْصَنَـٰتٍ غَيْرَ مُسَـٰفِحَـٰتٍ وَلَا مُتَّخِذَٰتِ أَخْدَانٍ ۚ فَإِذَآ أُحْصِنَّ فَإِنْ أَتَيْنَ بِفَـٰحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى ٱلْمُحْصَنَـٰتِ مِنَ ٱلْعَذَابِ ۚ ذَٰلِكَ لِمَنْ خَشِىَ ٱلْعَنَتَ مِنكُمْ ۚ وَأَن تَصْبِرُوا۟ خَيْرٌ لَّكُمْ ۗ وَٱللَّهُ غَفُورٌ رَّحِيمٌ','verse_number' => '25','verse_key' => '4:25','hizb_number' => '9','rub_el_hizb_number' => '33','ruku_number' => '65','manzil_number' => '1','sajdah_number' => '0','page_number' => '82','juz_number' => '5','created_at' => '2023-12-01 17:30:04','updated_at' => '2023-12-01 17:30:04'),
