        $verse_translations = array(
            array('id' => '1','chapter_id' => '1','translator_id' => '2','verse_number' => '1','translate_name' => 'আল্লাহর নাম নিয়ে (আরম্ভ করছি), (যিনি) রহমান (--পরম করুণাময়, যিনি অসীম করুণা ও দয়া বশতঃ বিশ্বজগতের সমস্ত সৃষ্টির সহাবস্থানের প্রয়োজনীয় সব ব্যবস্থা অগ্রিম করে রেখেছেন), (যিনি) রহীম (--অফুরন্ত ফলদাতা, যাঁর অপার করুণা ও দয়ার ফলে প্রত্যেকের ক্ষুদ্রতম শুভ-প্রচেষ্টাও বিপুলভাবে সাফল্যমণ্ডিত ও পুরস্কৃত হয়ে থাকে)।','created_at' => '2023-12-02 06:47:02','updated_at' => '2023-12-02 06:47:02'),
            array('id' => '2','chapter_id' => '1','translator_id' => '2','verse_number' => '2','translate_name' => 'সমস্ত প্রশংসা আল্লাহ্‌র প্রাপ্য, সমুদয় সৃষ্ট-জগতের রব্ব।','created_at' => '2023-12-02 06:47:02','updated_at' => '2023-12-02 06:47:02'),
