            array('id' => '1885', 'chapter_id' => '15', 'translator_id' => '2', 'verse_number' => '83', 'translate_name' => 'কিন্তু প্রচন্ড আওয়াজ তাদের পাকড়াও করল সকালবেলায়।', 'created_at' => '2023-12-02 06:47:03', 'updated_at' => '2023-12-02 06:47:03'),
