            array('id' => '1887', 'chapter_id' => '15', 'translator_id' => '2', 'verse_number' => '85', 'translate_name' => 'আর আমরা মহাকাশমন্ডলী ও পৃথিবী এবং এ দুইয়ের মধ্যে যা-কিছু আছে তা সৃষ্টি করি নি সত্যের সঙ্গে ব্যতীত। আর নিঃসন্দেহ ঘড়ি-ঘন্টা তো এসে পড়ল; সুতরাং উপেক্ষা করো মহৎ উপেক্ষাভরে।', 'created_at' => '2023-12-02 06:47:03', 'updated_at' => '2023-12-02 06:47:03'),
            array('id' => '1888', 'chapter_id' => '15', 'translator_id' => '2', 'verse_number' => '86', 'translate_name' => 'নিঃসন্দেহ তোমার প্রভু, -- তিনি সর্বস্রষ্টা, সর্বজ্ঞাতা।', 'created_at' => '2023-12-02 06:47:03', 'updated_at' => '2023-12-02 06:47:03'),
            array('id' => '1889', 'chapter_id' => '15', 'translator_id' => '2', 'verse_number' => '87', 'translate_name' => 'আর নিশ্চয়ই তোমাকে আমরা দিয়েছি বারবার-পঠিত সাতটি, আর এক সুমহান কুরআন।', 'created_at' => '2023-12-02 06:47:03', 'updated_at' => '2023-12-02 06:47:03'),
            array('id' => '1890', 'chapter_id' => '15', 'translator_id' => '2', 'verse_number' => '88', 'translate_name' => 'তাদের মধ্যের কতক পরিবারকে যা ভোগবিলাসের বস্তু দিয়েছি তার প্রতি তোমার চোখ দিয়ো না, আর তাদের প্রতি তুমি ক্ষোভ করো না, বরং তোমার ডানা নামাও মুমিনদের জন্য।', 'created_at' => '2023-12-02 06:47:03', 'updated_at' => '2023-12-02 06:47:03'),
            array('id' => '1891', 'chapter_id' => '15', 'translator_id' => '2', 'verse_number' => '89', 'translate_name' => 'আর বলো -- \'\'নিঃসন্দেহ আমি, আমি হচ্ছি একজন প্রকাশ্য সতর্ককারী।’’', 'created_at' => '2023-12-02 06:47:03', 'updated_at' => '2023-12-02 06:47:03'),
