            array('id' => '2738', 'chapter_id' => '23', 'translator_id' => '2', 'verse_number' => '65', 'translate_name' => '\'\'আজ আর্তনাদ করো না, নিঃসন্দেহ তোমাদের ক্ষেত্রে -- আমাদের থেকে তোমাদের সাহায্য করা হবে না।', 'created_at' => '2023-12-02 06:47:04', 'updated_at' => '2023-12-02 06:47:04'),
            array('id' => '2739', 'chapter_id' => '23', 'translator_id' => '2', 'verse_number' => '66', 'translate_name' => 'তোমাদের কাছে আমার বাণীসমূহ অবশ্যই পাঠ করা হত, কিন্ত তোমরা তোমাদের গোড়ালির উপরে মোড় ফিরে চলে যেতে --', 'created_at' => '2023-12-02 06:47:04', 'updated_at' => '2023-12-02 06:47:04'),
