            array('id' => '3803','chapter_id' => '37','translator_id' => '2','verse_number' => '15','translate_name' => 'আর বলে -- \'\'এটি স্পষ্ট জাদু বৈ তো নয়’’,','created_at' => '2023-12-02 06:47:04','updated_at' => '2023-12-02 06:47:04'),
            array('id' => '3804','chapter_id' => '37','translator_id' => '2','verse_number' => '16','translate_name' => '\'\'কী! যখন আমরা মারা যাব এবং ধূলোমাটি ও হাড়গোড় হয়ে যাব তখন কি আমরা ঠিকঠিকই পুনরুত্থিত হব?’’','created_at' => '2023-12-02 06:47:04','updated_at' => '2023-12-02 06:47:04'),
