            array('id' => '3808','chapter_id' => '37','translator_id' => '2','verse_number' => '20','translate_name' => 'আর তারা বলবে -- \'\'হায় ধিক্‌, আমাদের! এটিই তো বিচারের দিন!’’','created_at' => '2023-12-02 06:47:04','updated_at' => '2023-12-02 06:47:04'),
