            array('id' => '4671','chapter_id' => '50','translator_id' => '2','verse_number' => '41','translate_name' => 'আর শোনো সেইদিন যখন একজন ঘোষণাকারী আহ্বান করবেন নিকটবর্তী স্থান থেকে, --','created_at' => '2023-12-02 06:47:05','updated_at' => '2023-12-02 06:47:05'),
            array('id' => '4672','chapter_id' => '50','translator_id' => '2','verse_number' => '42','translate_name' => 'সেইদিন তারা সত্যি-সত্যি মহাগর্জন শুনতে পাবে। এইটিই বেরিয়ে আসার দিন।','created_at' => '2023-12-02 06:47:05','updated_at' => '2023-12-02 06:47:05'),
