            array('id' => '4747','chapter_id' => '52','translator_id' => '2','verse_number' => '12','translate_name' => 'যারা বৃথা তর্কবিতর্কে খেলা খেলছে।','created_at' => '2023-12-02 06:47:05','updated_at' => '2023-12-02 06:47:05'),
