            array('id' => '4778','chapter_id' => '52','translator_id' => '2','verse_number' => '43','translate_name' => 'অথবা আল্লাহ্ ছাড়া তাদের কি অন্য উপাস্য রয়েছে? আল্লাহ্‌রই সব মহিমা -- তারা যা শরিক করে তিনি তার বাইরে।','created_at' => '2023-12-02 06:47:05','updated_at' => '2023-12-02 06:47:05'),
            array('id' => '4779','chapter_id' => '52','translator_id' => '2','verse_number' => '44','translate_name' => 'আর যদি তারা দেখে আকাশের এক টুকরো ভেঙ্গে পড়ছে, তাহলে তারা বলবে -- \'\'এক পুঞ্জীভূত মেঘমালা।’’','created_at' => '2023-12-02 06:47:05','updated_at' => '2023-12-02 06:47:05'),
